    };
  };

  componentDidMount() {
    const u = firebase.database.doc()
  }
}*/


function Mainpage() {
  return (
    <div>
        <h1>Mainpage</h1> 
        <Link to='/login'>Sign in</Link>
        {fire.firestore().collection('articles').get()
        .then(data => {
        let art = [];
        data.forEach(doc => {
        art.push(doc.data());
        });
        art.forEach(a => {
          console.log(a.creationdate.toDate());
          <Singlearticle title={a.title} description = {a.description} text = {a.text} creationdate = {a.creationdate.toDate()} editdate = {a.editdate.toDate()} uid = {a.uid} />;
        });
        })}

        
    </div>
  );
}

export default Mainpage;
