import React, {Component} from 'react';
import './App.css';
/*import withStyles from '@material-ui/core/styles/withStyles';*/
/*import Grid from 'react-router-dom';*/
/*import axios from 'axios'*/
//import auth from 'firebase/firebase-auth'
import firebase from './firebase.js';



class Login extends Component {
  constructor(props) {
    super(props);
    this.login = this.login.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.signup = this.signup.bind(this);
    this.state = {
      email:'',
      password:'',
      uid: ''
    }
  }

  componentDidMount() {

    firebase.auth().onAuthStateChanged((user) => {
        if(user) {
          this.setState({uid : user.uid}); 
        } else {
          this.setState({uid: null});
        }
        });
  };

  login(e) {
    e.preventDefault();
    firebase.auth().signInWithEmailAndPassword(this.state.email, this.state.password).then((u) =>{
    }).catch((error) => {
      console.log(error);
    });
    this.props.history.push('/articles');

  }

  signup(e) {
    e.preventDefault();
    firebase.auth().createUserWithEmailAndPassword(this.state.email, this.state.password)
    .catch((error) => {
      console.log(error);
    });
    this.props.history.push('/articles');
  }

  handleChange(e) {
    this.setState({[e.target.name]: e.target.value});
  }
  
  render() {
    if(this.state.uid == null)
    {
      return(
        <div>
        <h1>Sign in</h1>
        <form>
          <label>Email</label><br/>
          <input id="email" name="email" type="email" value={this.state.email} onChange={this.handleChange}/><br/>
          <label>Password</label><br/>
          <input id="password" name="password" type="password" value={this.state.password} onChange={this.handleChange}/><br/>
          <button type="button" onClick={this.login}>Sign in</button>
          <button type="button" onClick={this.signup}>Sign up</button>
        </form>
        </div>
      );
    }
    else if (this.state.uid != null){
      return(
        <div>
          <h1>Вы уже в сети</h1>
        </div>
      );
    }
    
  }
} 

