import react from 'react'
import firebase from 'firebase.js'

class UserArticle extends React.Component {
    constructor(props)
    {
        this.edit = this.edit.bind(this);
    }
    edit() {
        database().ref('/users/-M8RjTZgI5ROXg4h1KaK').update({
                title: 'Измененная статья',
                description: 'Измененное описание',
                text: 'Измененный текст'
            })
        .then(() => console.log('Data updated.'));
    }

    render() 
    {
        return(
            <div>
                <button type="button" onClick={this.edit}></button>
            </div>
        )
    }
}
