class App extends Component {
  constructor(props) {
      super(props);
      this.state = {
          postinfo: []
      }

  }

  componentDidMount() {
      let newState = [];
      const db = firebase.database();
      const postdata = db.ref('karetnikova-practice1-20c78');

      postdata.on('value', (snapshot) =>
      {
          let posts = snapshot.val();
          for(let post in posts)
          {
              newState.push({
                  id: post,
                  title: posts[post].title,
                  text: posts[post].text
              });
          }
          this.setState({
              postinfo: newState
          });
      });
  }

  render() {
      return (
          <div>
              {this.state.postinfo.map((postinfo) => {
                  return (
                      <div>
                          <div>
                              <h1>{postinfo.title}</h1>
                              <p>{postinfo.title}</p>
                          </div>
                      </div>
                  )
              })}
          </div>
      );
  }
}
