import React, {Component} from 'react';
import './App.css';
/*import withStyles from '@material-ui/core/styles/withStyles';*/
/*import Grid from 'react-router-dom';*/
import axios from 'axios'


class Login extends Component {

  constructor() {
    super();
    this.state = {
      email: '',
      password: ''
    }
  }

  /*handleSubmit = (event) => {
    event.preventDerault();
    this.setState({
      loading: true
    });
    const userData = {
      email: this.state.email,
      password: this.state.password
    }
    axios.post('/login', userData)
      .then(res => {
        console.log(res.data);
        this.setState({
         loading: false 
        });
        this.props.history.push('/');
      })
      .catch(err => {
        this.setState({
          errors: err.response.data,
          loading: false
        });
      })
  }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    });
  }*/
  render() {
    /*const {errors, loading} = this.state;*/
    return(
      <div>
      <h1>Sign in</h1>
      <form onSubmit={this.handleSubmit}>
        <label>Email</label>
        <input id="email" name="email" type="email" value={this.state.email} onChange={this.handleChange}/><br/>
        <label>Password</label>
        <input id="password" name="password" type="password" label="Password" value={this.state.password} onChange={this.handleChange}/>
        <button type="submit">Sign in</button>
      </form>
      </div>
    );
  }
}
/*function Login() {
  return (
    <div>
      <h1>Sign in</h1>
      <form onSubmit={this.handleSubmit}>
        <input id="email" name="email" type="email" label="Email" />

      </form>
      <p>Username</p>
      <input type="text"/>
      <p>Password</p>
      <input type="password"/>
      <button>Sign in</button>
    </div>
  );
}*/

export default Login;
