import React from 'react';
import firebase from './firebase.js'
import EditArticle from './EditArticle'

class UserArticle extends React.Component {
    constructor(props)
    {
        super(props);
        this.edit = this.edit.bind(this);
        this.id = props.id;
        this.state = {
            title: '', 
            description: '',
            text: ''
        }
    }
    edit() 
    {
        firebase.database().ref('/posts/-M8RjTZgI5ROXg4h1KaK').update({
                title: '1',
                description: '2',
                text: '3'
            })
    }

    delete() 
    {
        firebase.database().ref('/posts/-M8RjTZgI5ROXg4h1KaK').remove();
    }

    handleChange(e) {
        this.setState({[e.target.name]: e.target.value});
    }

    componentDidMount() {
        let newState = [];
        const db = firebase.database();
        const postdata = db.ref('posts');

        postdata.on('value', (snapshot) =>
        {
            let posts = snapshot.val();
            for(let post in posts)
            {
                newState.push({
                    id: post,
                    title: posts[post].title,
                    text: posts[post].text
                });
            }
            this.setState({
                postinfo: newState
            });
        });
    };

    render() 
    {
        /*return(
            <div>
                <button type="button" onClick={this.edit}>Edit</button>
                <button type="button" onClick={this.delete}>Delete</button>
            </div>
        )*/

        /*return (
        <div>
        <form>

          <button type="button" onClick={<EditArticle />}>Edit</button>
          <button type="button" onClick={this.delete}>Delete</button>
        </form>
        <hr/>
        </div>
        )*/
        return (
            <div>
                {this.state.postinfo.map((postinfo) => {
                    return (
                        <div>
                            <div>
                                <h1>{postinfo.title}</h1>
                                <p>{postinfo.id}</p>
                                <p>{postinfo.text}</p>
                            </div>
                            <button type="button" onClick={<EditArticle />}>Edit</button>
                             <button type="button" onClick={this.delete}>Delete</button>
                            <hr/>
                        </div>
                    )
                })}
            </div>
        );
    }
}

export default UserArticle;
