class NewArticle extends Component {
    constructor()
    {
      super();
      this.handleChange = this.handleChange.bind(this);
      this.pushing = this.pushing.bind(this);
      this.state = {
        title: '',
        description: '',
        text: ''
      }
    }

    pushing() 
    {
      DatabaseReference; newReference = firebase.database()
      .ref("posts")
      .push()

      newReference
      .set({
        title: this.state.title,
        description: this.state.description,
        text: this.state.text
      });
      /*firebase.database().ref('posts/' + this.state.title).set({
        title: this.state.title,
        description: this.state.description,
        text: this.state.text
      });*/

    }

    handleChange(e) {
      this.setState({[e.target.name]: e.target.value});
    }

    /*render() {
        return(
            <div>
                <button onClick={this.pushing}>Add</button>
                <h1>Yeaaaah!</h1>
                
            </div>
            
        );
    }*/
    render() {

      return(
        <div>
        <h1>Страница добавления статьи</h1>
        <form>
          <label>Заголовок</label><br/>
          <input id="title" name="title" type="text" value={this.state.title} onChange={this.handleChange}/><br/>
          <label>Описание</label><br/>
          <input id="description" name="description" type="text" value={this.state.description} onChange={this.handleChange}/><br/>
          <label>Текст</label><br/>
          <input id="text" name="text" type="text" value={this.state.maintext} onChange={this.handleChange}/><br/>
          <button type="submit" onClick={this.pushing}>Добавить</button>
        </form>

        </div>
  
      );
    }
}

export default NewArticle;
