import React from 'react';
import firebase from './firebase.js';
import Menu from './Menu';
import '../src/css/SingleArticle.css';


class SingleArticle extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            title: '', 
            description: '',
            text: '',
            creationdate: '',
            editdate: null
        }
    }

    componentDidMount() {
        firebase.database()
        .ref('posts/' + this.props.match.params.id)
        .once('value')
        .then(snapshot => {
            this.setState ({
                title: snapshot.val().title,
                description: snapshot.val().description,
                text: snapshot.val().text,
                creationdate: snapshot.val().creationdate,
                editdate: snapshot.val().editdate
            })
        });
    }

    render() {
        return(
            <div>
                <Menu />
                <div class="header">
                    <div class="header-title-container">
                        <h1 class="main-title">{this.state.title}</h1>
                        <h2 class="description">{this.state.description}</h2>
                        <span>Опубликовано: {this.state.creationdate}</span>
                    </div>
                </div>
                <p>{this.state.text}</p>
                <p>{this.state.editdate}</p>
            </div>
        )
    }

}

export default SingleArticle;
