import './App.css';
import propTypes from 'prop-types';

class article extends Component {
    constructor(props){
        super(props);
        this.mesage = "Article component!";
    }

    render() {
        return (
            <div>
                <h1>{this.message}</h1>
            </div>
            
        )
    }
}



