import React from 'react';
import firebase from './firebase.js'
import EditArticle from './EditArticle'

class UserArticle extends React.Component {
    constructor(props)
    {
        super(props);

        this.state = {
            postinfo: [],
            title: '', 
            description: '',
            text: '',
            uid: ''
        }
    }

    componentDidMount() {

        firebase.auth().onAuthStateChanged((user) => {
            if(user) {
              this.setState({uid : user.uid}); 
            } else {
              this.setState({uid: null});
            }
            });

            let newState = [];
            const db = firebase.database();
            const postdata = db.ref('posts/');

            postdata.on('value', (snapshot) =>
            {
                let posts = snapshot.val();
                for(let post in posts)
                {
                    newState.push({
                        id: post,
                        title: posts[post].title,
                        description: posts[post].description,
                        text: posts[post].text,
                        creationdate: posts[post].creationdate,
                        editdate: posts[post].editdate
                    });
                }
                this.setState({
                    postinfo: newState
                });
            });
    };

    delete(id) 
    {
        firebase.database().ref('posts/' + id).remove();
    }

    handleChange(e) {
        this.setState({[e.target.name]: e.target.value});
    }

    render() 
    {
        /*return(
            <div>
                <button type="button" onClick={this.edit}>Edit</button>
                <button type="button" onClick={this.delete}>Delete</button>
            </div>
        )*/

        /*return (
        <div>
        <form>

          <button type="button" onClick={<EditArticle />}>Edit</button>
          <button type="button" onClick={this.delete}>Delete</button>
        </form>
        <hr/>
        </div>
        )*/
        return (
            <div>
                {this.state.postinfo.map((postinfo) => {
                    if(postinfo.uid === this.state.uid)
                    {
                        return (
                            <div>
                                <div>
                                    <h1>{postinfo.title}</h1>
                                    <p>{postinfo.id}</p>
                                    <p>{postinfo.text}</p>
                                </div>
                                <button type="button" onClick={<EditArticle />}>Edit</button>
                                <button type="button" onClick={this.delete}>Delete</button>
                                <hr/>
                            </div>
                        )
                    }
                    
                })}
            </div>
        );
    }
}

export default UserArticle;
