import React from 'react';
import firebase from './firebase.js';
import './css/Menu.css';

class Menu extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            uid: ''
        }
    }

    componentDidMount() {

        firebase.auth().onAuthStateChanged((user) => {
            if(user) {
              this.setState({uid : user.uid}); 
            } else {
              this.setState({uid: null});
            }
        });
    };
    
    render() {
        if(this.state.uid == null) {
            return(
                <div>
                    <nav>
                        <ul class="menuItems">
                        <li><a href='#' data-item='Home'>Главная страница</a></li>
                        <li><a href='#' data-item='About'>Вход</a></li>
                        </ul>
                    </nav>

                </div>
            );
        }
        else {
            return(
                <div>
                    <nav>
                        <ul class="menuItems">
                        <li><a href='#' data-item='Home'>Главная страница</a></li>
                        <li><a href='#' data-item='About'>Выход</a></li>
                        <li><a href='#' data-item='Projects'>Мои статьи</a></li>
                        <li><a href='#' data-item='Blog'>Настройки</a></li>
                        </ul>
                    </nav>

                </div>
            );
        }
    } 
}

export default Menu;
