import React, {Component} from 'react';
import './App.css';
//import propTypes from 'prop-types';
//import * as firebase from 'firebase';

class Singlearticle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            articles: []
        };
    }

    readfromDB() {
        fire.firestore().collection('articles').get()
        .then(data => {
        let art = [];
        data.forEach(doc => {
        this.articles.push(doc.data());
        });
        })
    }

    render() {
        this.readfromDB();
        return (
            <div>
            {articles.forEach( article => {
                return (
                    <div>
                        <p>{article.title}</p>
                        <p>{article.description}</p>
                        <p>{article.text}</p>
                        <p>{article.creationdate.toDate()}</p>
                        <p>{article.editdate.toDate()}</p>
                        <p>{article.uid}</p>
                    </div>
                )

            })}
            
            </div> 
        );
    }
}
