import React from 'react';
import firebase from './firebase.js'

class UserArticle extends React.Component {
    constructor(props)
    {
        super(props);
        this.edit = this.edit.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleClick = this.handleClick.bind(this);
        //this.id = props.id;
        this.state = {
            title: '', 
            description: '',
            text: ''
        }
    }

    componentDidMount() {
        let newState = [];
        const db = firebase.database();
        const postdata = db.ref('posts/' + this.props.match.params.id);

        const title = postdata.get({title: 'title'});
        console.log(title.title);

    }
    edit() 
    {
        var date = new Date();
        var day = date.getDate();
        var month = date.getMonth()+1;
        var year = date.getFullYear();


        firebase.database().ref('posts/' + this.props.match.params.id).update({
                title: this.state.title,
                description: this.state.description,
                text: this.state.text,
                editdate: day + "." + month + "." + year
            })
        
        this.props.history.push('/articles')
    }

    handleChange(e) {
        this.setState({[e.target.name]: e.target.value});
      }

    handleClick(e) {
        this.props.history.push('/articles');
    }

    render() 
    {
        /*return(
            <div>
                <button type="button" onClick={this.edit}>Edit</button>
                <button type="button" onClick={this.delete}>Delete</button>
            </div>
        )*/
        return (
        <div>
            <h1>Страница редактирования статьи</h1>
            <form>
            <label>Заголовок</label><br/>
            <input id="title" name="title" type="text" value={this.state.title} onChange={this.handleChange}/><br/>
            <label>Описание</label><br/>
            <input id="description" name="description" type="text" value={this.state.description} onChange={this.handleChange}/><br/>
            <label>Текст</label><br/>
            <textarea id="text" name="text" value={this.state.maintext} onChange={this.handleChange}></textarea><br/>
            
            <button type="button" onClick={this.edit}>Edit</button>
            <button type="button" onClick={this.handleClick}>Отмена</button>
            </form>

        </div>
        )
    }
}

export default UserArticle;
