    };
  };

  componentDidMount() {
    const u = firebase.database.doc()
  }
}*/


class Mainpage extends Component {
  constructor(props){
    super(props);
    this.state = {
      user: {},
    }
  }

  componentDidMount() {
    this.authListener();
  }
  authListener() {
    fire.auth().onAuthStateChanged((user) => {
     // console.log(user);
      if(user) {
        this.setState({user});
    //    localStorage.setItem('user', user.uid);
      } else {
        this.setState({user: null});
    //    localStorage.removeItem('user');
      }
      });
  }

  render() {
    return(
      <div>
    <h1>Mainpage</h1> 
    <Link to='/login'>Sign in</Link>
    {this.state.user ? ( <Articles/>) : (<Mainpage />)}
    </div>
    )
  };
}
