import React, {Component} from 'react';
import './App.css';
/*import withStyles from '@material-ui/core/styles/withStyles';*/
/*import Grid from 'react-router-dom';*/
/*import axios from 'axios'*/
import auth from 'firebase/firebase-auth'


class Login extends Component {

  constructor() {
    super();
    this.state = {
      email: '',
      password: ''
    }
  }
  
  handleSubmit() {
    auth()
  .createUserWithEmailAndPassword(this.email, this.password)
  .then(() => {
    console.log('User account created & signed in!');
  })
  .catch(error => {
    if (error.code === 'auth/email-already-in-use') {
      console.log('That email address is already in use!');
    }

    if (error.code === 'auth/invalid-email') {
      console.log('That email address is invalid!');
    }

    console.error(error);
  });
  } 
  /*handleSubmit = (event) => {
    event.preventDerault();
    this.setState({
      loading: true
    });
    const userData = {
      email: this.state.email,
      password: this.state.password
    }
    axios.post('/login', userData)
      .then(res => {
        console.log(res.data);
        this.setState({
         loading: false 
        });
        this.props.history.push('/');
      })
      .catch(err => {
        this.setState({
          errors: err.response.data,
          loading: false
        });
      })
  }*/

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    });
  }

  render() {
    /*const {errors, loading} = this.state;*/
    return(
      <div>
      <h1>Sign in</h1>
      <form onSubmit={auth()
  .createUserWithEmailAndPassword('sarah.lane@gmail.com', 'SuperSecretPassword!')
  .then(() => {
    console.log('User account created & signed in!');
  })
  .catch(error => {
    if (error.code === 'auth/email-already-in-use') {
      console.log('That email address is already in use!');
    }

    if (error.code === 'auth/invalid-email') {
      console.log('That email address is invalid!');
    }

    console.error(error);
  })}>
        <label>Email</label>
        <input id="email" name="email" type="email" value={this.state.email} onChange={this.handleChange}/><br/>
        <label>Password</label>
        <input id="password" name="password" type="password" label="Password" value={this.state.password} onChange={this.handleChange}/>
        <button type="submit">Sign in</button>
      </form>
      </div>
    );
  }
}
