import React from 'react';
import './App.css';
import {Link} from 'react-router-dom'
import article from './article'
import fire from './Fire';

/*class ComponentToPrint extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      users: []
    };
  };

  componentDidMount() {
    const u = firebase.database.doc()
  }
}*/


function Mainpage() {
  return (
    <div>
        <h1>Mainpage</h1> 
        <Link to='/login'>Sign in</Link>
        <p id="title"></p>
            <p id="description"></p>
            <p id="text"></p>
            <p id="creationdate"></p>
            <p id="editdate"></p>
            <p id="uid"></p>
        {fire.firestore().collection('articles').get()
        .then(data => {
          data.forEach(doc => {
            document.getElementById("title").innerHTML = doc.data.title
            document.getElementById("description").innerHTML = doc.data.description
            document.getElementById("text").innerHTML = doc.data.text
            document.getElementById("creationdate").innerHTML = doc.data.creationdate.toDate()
            document.getElementById("editdate").innerHTML = doc.data.editdate.toDate()
            document.getElementById("uid").innerHTML = doc.data.uid;
          });
        })}
    </div>
  );
}

export default Mainpage;
