import React, {Component} from 'react';
import './App.css';
//import propTypes from 'prop-types';
import firebase from './firebase.js'
import '../src/css/AllArticles.css';
import {Link} from 'react-router-dom';

class Singlearticle extends Component {
    constructor(props) {
        super(props);
        this.state = {
            postinfo: [],
        };
        
    };

    componentDidMount() {
        let newState = [];
        const db = firebase.database();
        const postdata = db.ref('posts/');

        postdata.on('value', (snapshot) =>
        {
            let posts = snapshot.val();
            for(let post in posts)
            {
                newState.push({
                    id: post,
                    title: posts[post].title,
                    description: posts[post].description,
                    text: posts[post].text,
                    creationdate: posts[post].creationdate,
                    editdate: posts[post].editdate
                });
            }
            this.setState({
                postinfo: newState
            });
        });
    };

    render()
    {
        return (
            <div class="container">
                {this.state.postinfo.map((postinfo) => {
                    return (
                        <div class="post">
                                <a href=''>
                                    <Link to={/article/+ postinfo.id}>
                                        <h2 class="title">{postinfo.title}</h2>
                                        <h3 class="description">{postinfo.description}</h3>
                                    </Link>
                                </a>
                                {/*<p>{postinfo.text}</p>
                                <p>{postinfo.creationdate}</p>
                                 <p>{postinfo.editdate}</p>*/}
                           {/*<button type="button"><Link to={/article/+ postinfo.id}>Читать полностью</Link></button>*/}
                            <div class="line"></div>
                        </div>
                    )
                })}
            </div>
        );
    }

}
