import React from 'react';
import firebase from './firebase.js'
import EditArticle from './EditArticle'

class UserArticle extends React.Component {
    constructor(props)
    {
        super(props);
        this.edit = this.edit.bind(this);
        this.id = props.id;
        this.state = {
            title: '', 
            description: '',
            text: ''
        }
    }
    edit() 
    {
        firebase.database().ref('/posts/-M8RjTZgI5ROXg4h1KaK').update({
                title: '1',
                description: '2',
                text: '3'
            })
    }

    /*delete() 
    {
        firebase.database().ref('/posts/-M8RjTZgI5ROXg4h1KaK').remove();
    }

    handleChange(e) {
        this.setState({[e.target.name]: e.target.value});
    }*/

    render() 
    {
        /*return(
            <div>
                <button type="button" onClick={this.edit}>Edit</button>
                <button type="button" onClick={this.delete}>Delete</button>
            </div>
        )*/
        return (
        <div>
        <form>
          <label>Заголовок</label><br/>
          <input id="title" name="title" type="text" value={this.state.title} onChange={this.handleChange}/><br/>
          <label>Описание</label><br/>
          <input id="description" name="description" type="text" value={this.state.description} onChange={this.handleChange}/><br/>
          <label>Текст</label><br/>
          <input id="text" name="text" type="text" value={this.state.maintext} onChange={this.handleChange}/><br/>
          <button type="button" onClick={this.edit}>Edit</button>
          <button type="button" onClick={<EditArticle />}>Delete</button>
        </form>
        <hr/>
        </div>
        )
    }
}

export default UserArticle;
