import React, {Component} from 'react';
import './App.css';
import firebase from './firebase.js';
/*import * as f from 'firebase';
import AllArticles from './AllArticles';
import NewArticle from './NewArticle';
import UserArticle from './UserArticle';
import EditArticle from './EditArticle';*/
import {Link} from 'react-router-dom';
import Menu from './Menu';
//import App from './App'
//import Mainpage from './Mainpage'
//import {Link} from 'react-router-dom'
import Header from './Header';
import '../src/css/AllArticles.css';

class Articles extends Component {
  constructor(props) {
      super(props);
      this.delete = this.delete.bind(this);
      this.logout = this.logout.bind(this);
      this.state = {
          postinfo: [],
          uid: '', 
      };
      
  };


 /* componentDidMount() {

    firebase.auth().onAuthStateChanged((user) => {
        if(user) {
          this.setState({uid : user.uid}); 
        } else {
          this.setState({uid: null});
        }
        });

      let newState = [];
      const db = firebase.database();
      const postdata = db.ref('posts/' + this.state.uid);

      postdata.on('value', (snapshot) =>
      {
          let posts = snapshot.val();
          for(let post in posts)
          {
              newState.push({
                  id: post,
                  title: posts[post].title,
                  text: posts[post].text
              });
          }
          this.setState({
              postinfo: newState
          });
      });
  };*/

  componentDidMount() {

    firebase.auth().onAuthStateChanged((user) => {
        if(user) {
          this.setState({uid : user.uid}); 
        } else {
          this.setState({uid: null});
        }
        });

        let newState = [];
        const db = firebase.database();
        const postdata = db.ref('posts/');

        postdata.on('value', (snapshot) =>
        {
            let posts = snapshot.val();
            for(let post in posts)
            {
                newState.push({
                  id: post,
                  title: posts[post].title,
                  description: posts[post].description,
                  text: posts[post].text,
                  creationdate: posts[post].creationdate,
                  editdate: posts[post].editdate,
                  uid: posts[post].uid
                });
            }
            this.setState({
                postinfo: newState
            });
        });
  };

  delete = param => e => 
  {
    firebase.database().ref('posts/' + param).remove();
    //window.location.reload();
    this.componentDidMount();
    //this.forceUpdate();
    console.log('Delete');
  }

  logout() {
    //firebase.auth().signOut();
    firebase.auth().signOut().then(function() {
      console.log('Signed Out');
    }, function(error) {
      console.error('Sign Out Error', error);
    });
    this.props.history.push('/');
  }

 
  render() {
        return (
          
          <div>
            <Menu />
            <Header />
            {/*<h1>Articles</h1>
            <button type="button" onClick={this.logout}>Logout</button>*/}
            <div class="container">
              {this.state.postinfo.map((postinfo) => {

                if(postinfo.uid === this.state.uid)
                {
                  return (
                    <div key={postinfo.id} class="post">
                      <Link to={/article/+ postinfo.id} class="link">
                        <h2 class="title">{postinfo.title}</h2>
                        <h3 class="description">{postinfo.description}</h3>
                      </Link>
                        {/*<p>{postinfo.text}</p>
                        <p>{postinfo.creationdate}</p>
                        <p>{postinfo.editdate}</p>
                        <h1>{postinfo.id}</h1>*/}
                        <button type="button" onClick={this.delete(postinfo.id)}>Delete</button>
                        <button type="button"><Link to={/edit/+ postinfo.id}>Edit</Link></button>
                        <div class="line"></div>
                    </div>
                );
                }
                  
              })}
              </div>
              <button type="button"><Link to='/addarticle'>Добавить новую статью</Link></button>
          </div>
      );  

      
  }
}

