import React from 'react';
import firebase from './firebase.js'

class UserArticle extends React.Component {
    constructor(props)
    {
        super(props);
        this.edit = this.edit.bind(this);
    }
    edit() 
    {
        firebase.database().ref('/posts/-M8RjTZgI5ROXg4h1KaK').update({
                title: '1',
                description: '2',
                text: '3'
            })
    }

    delete() 
    {
        firebase.database().ref('/users/123').remove();
    }

    render() 
    {
        return(
            <div>
                <button type="button" onClick={this.edit}>Edit</button>
                <button type="button" onClick={this.delete}>Delete</button>
            </div>
        )
    }
}

export default UserArticle;
