import React from 'react';
import './App.css';
import {Link} from 'react-router-dom'
import article from './article'
import fire from './Fire'

/*class ComponentToPrint extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      users: []
    };
  };

  componentDidMount() {
    const u = firebase.database.doc()
  }
}*/


function Mainpage() {
  return (
    <div>
        <h1>Mainpage</h1> 
        <Link to='/login'>Sign in</Link>
        <p id="title"></p>
            <p id="description"></p>
            <p id="text"></p>
            <p id="creationdate"></p>
            <p id="editdate"></p>
            <p id="uid"></p>
        <div>{fire.firestore().collection('articles').doc('GVmj16Wlg9SpbnDglbD8').get()
        .then(doc => {
            const d = doc.data();
            document.getElementById("title").innerHTML = d.title
            document.getElementById("description").innerHTML = d.description;
            document.getElementById("text").innerHTML = d.text;
            document.getElementById("creationdate").innerHTML = d.creationdate.toDate();
            document.getElementById("editdate").innerHTML = d.editdate.toDate();
            document.getElementById("uid").innerHTML = d.uid;
        })}</div>
    </div>
  );
}

export default Mainpage;
