import React from 'react';
import firebase from './firebase.js'
import EditArticle from './EditArticle'

class UserArticle extends React.Component {
    constructor(props)
    {
        super(props);
        this.delete = this.delete.bind(this);
        this.state = {
            title: '', 
            description: '',
            text: '',
            uid: ''
        }
    }

    componentDidMount() {

        firebase.auth().onAuthStateChanged((user) => {
            if(user) {
              this.setState({uid : user.uid}); 
            } else {
              this.setState({uid: null});
            }
            });
    };

    delete(id) 
    {
        firebase.database().ref('posts/' + id).remove();
    }

    handleChange(e) {
        this.setState({[e.target.name]: e.target.value});
    }

    render() 
    {
        /*return(
            <div>
                <button type="button" onClick={this.edit}>Edit</button>
                <button type="button" onClick={this.delete}>Delete</button>
            </div>
        )*/

        /*return (
        <div>
        <form>

          <button type="button" onClick={<EditArticle />}>Edit</button>
          <button type="button" onClick={this.delete}>Delete</button>
        </form>
        <hr/>
        </div>
        )*/
        return (
            <div>
                {this.state.postinfo.map((postinfo) => {
                    if(postinfo.uid === this.state.uid)
                    {
                        return (
                            <div>
                                <div>
                                    <h1>{postinfo.title}</h1>
                                    <p>{postinfo.id}</p>
                                    <p>{postinfo.text}</p>
                                </div>
                                <button type="button" onClick={<EditArticle />}>Edit</button>
                                <button type="button" onClick={this.delete}>Delete</button>
                                <hr/>
                            </div>
                        )
                    }
                    
                })}
            </div>
        );
    }
}

export default UserArticle;
