import React, {Component} from 'react';
import './App.css';
/*import withStyles from '@material-ui/core/styles/withStyles';*/
/*import Grid from 'react-router-dom';*/



class Login extends Component {

  constructor() {
    super();
    this.state = {
      email: '',
      password: '',
      loading: false,
      errors: {}
    }
  }

  handleSubmit = (event) => {
    event.preventDerault();
    console.log('You did it! Yeaaaaaah!');
  }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    });
  }
  render() {
    return(
      <div>
      <h1>Sign in</h1>
      <form onSubmit={this.handleSubmit}>
        <label>Email</label>
        <input id="email" name="email" type="email" value={this.state.email} onChange={this.handleChange}/><br/>
        <label>Password</label>
        <input id="password" name="password" type="password" label="Password" value={this.state.password} onChange={this.handleChange}/>
        <button type="submit">Sign in</button>
      </form>
      </div>
    );
  }
}
